mapper.select:
SELECT TO_CHAR(PAYDATE,'YYYY-MM-DD') PAYDATE, SALEAMOUNT FROM( SELECT PAYDATE, SUM(PRICE-DCPRICE) SALEAMOUNT FROM ( SELECT PAYDATE, PRICE, DCPRICE FROM PAYMENT
WHERE PAYDATE >= #{startDate} AND PAYDATE <= #{endDate}
ORDER BY PAYDATE ) GROUP BY ROLLUP(PAYDATE) ORDER BY PAYDATE ) WHERE PAYDATE IS NOT NULL

SELECT TO_CHAR(REPAIRDATE,'YYYY-MM-DD') REPAIRDATE, SALEAMOUNT FROM( SELECT REPAIRDATE, SUM(COST) SALEAMOUNT FROM ( SELECT REPAIRDATE, COST FROM REPAIR
WHERE REPAIRDATE >= #{startDate} AND REPAIRDATE <= #{endDate}
ORDER BY REPAIRDATE ) GROUP BY ROLLUP(REPAIRDATE) ORDER BY REPAIRDATE ) WHERE REPAIRDATE IS NOT NULL

SELECT TO_CHAR(INDATE,'YYYY-MM-DD') INDATE, SALEAMOUNT FROM( SELECT INDATE, SUM(QUANTITY * UNITPRICE) SALEAMOUNT FROM ( SELECT INDATE, QUANTITY, UNITPRICE FROM GOODSIN
WHERE INDATE >= #{startDate} AND INDATE <= #{endDate}
ORDER BY INDATE ) GROUP BY ROLLUP(INDATE) ORDER BY INDATE ) WHERE INDATE IS NOT NULL
