mapper.select:
SELECT TO_CHAR(PAYDATE,'YYYY-MM-DD') PAYDATE, SALEAMOUNT FROM( SELECT PAYDATE, SUM(PRICE-DCPRICE) SALEAMOUNT FROM ( SELECT PAYDATE, PRICE, DCPRICE, STATE FROM PAYMENT P JOIN CARDINFO C ON P.PAYCODE = P.PAYCODE
WHERE PAYDATE >= #{startDate} AND PAYDATE <= #{endDate}
ORDER BY PAYDATE ) WHERE STATE = '카드승인' GROUP BY ROLLUP(PAYDATE) ORDER BY PAYDATE ) WHERE PAYDATE IS NOT NULL

SELECT TO_CHAR(REPAIRDATE,'YYYY-MM-DD') REPAIRDATE, SALEAMOUNT FROM( SELECT REPAIRDATE, SUM(COST) SALEAMOUNT FROM ( SELECT REPAIRDATE, COST FROM REPAIR
WHERE REPAIRDATE >= #{startDate} AND REPAIRDATE <= #{endDate}
ORDER BY REPAIRDATE ) GROUP BY ROLLUP(REPAIRDATE) ORDER BY REPAIRDATE ) WHERE REPAIRDATE IS NOT NULL

SELECT TO_CHAR(INDATE,'YYYY-MM-DD') INDATE, SALEAMOUNT FROM( SELECT INDATE, SUM(QUANTITY * UNITPRICE) SALEAMOUNT FROM ( SELECT INDATE, QUANTITY, UNITPRICE FROM GOODSIN
WHERE INDATE >= #{startDate} AND INDATE <= #{endDate}
ORDER BY INDATE ) GROUP BY ROLLUP(INDATE) ORDER BY INDATE ) WHERE INDATE IS NOT NULL

SELECT TO_CHAR(PAYDATE,'YYYY-MM-DD') PAYDATE, SALEAMOUNT FROM( SELECT PAYDATE, SUM(PRICE-DCPRICE) SALEAMOUNT FROM ( SELECT P.PAYDATE, P.PRICE, P.DCPRICE, C.STATE FROM PAYMENT P JOIN CARDINFO C ON P.PAYCODE = P.PAYCODE left outer join paymentinfo pi on pi.paycode = p.paycode left outer join goods g on g.goodscode = pi.goodscode left outer join goodsgubun gg on g.gubuncode = gg.gubuncode WHERE gg.gubuncode IN ( SELECT gubuncode FROM goodsgubun WHERE gubuncode != 1 START WITH gubuncode= 1 CONNECT BY PRIOR gubuncode=parentcode )
AND PAYDATE >= #{startDate} AND PAYDATE <= #{endDate}
ORDER BY PAYDATE ) WHERE STATE = '카드승인' GROUP BY ROLLUP(PAYDATE) ORDER BY PAYDATE ) WHERE PAYDATE IS NOT NULL

SELECT TO_CHAR(PAYDATE,'YYYY-MM-DD') PAYDATE, SALEAMOUNT FROM( SELECT PAYDATE, SUM(PRICE-DCPRICE) SALEAMOUNT FROM ( SELECT P.PAYDATE, P.PRICE, P.DCPRICE, C.STATE FROM PAYMENT P JOIN CARDINFO C ON P.PAYCODE = P.PAYCODE left outer join paymentinfo pi on pi.paycode = p.paycode left outer join goods g on g.goodscode = pi.goodscode left outer join goodsgubun gg on g.gubuncode = gg.gubuncode WHERE gg.gubuncode IN ( SELECT gubuncode FROM goodsgubun WHERE gubuncode != 2 START WITH gubuncode= 2 CONNECT BY PRIOR gubuncode=parentcode )
AND PAYDATE >= #{startDate} AND PAYDATE <= #{endDate}
ORDER BY PAYDATE ) WHERE STATE = '카드승인' GROUP BY ROLLUP(PAYDATE) ORDER BY PAYDATE ) WHERE PAYDATE IS NOT NULL
