mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			    SELECT MAGAMY AS YEAR, MAGAMM AS MONTH, ISMAGAM, S.USERSCODE, NAME, TO_CHAR(MAGAMDATE,'YYYY-MM-DD') MAGAMDATE
				FROM MAGAM M
				JOIN STAFF S ON M.USERSCODE = S.USERSCODE
                ORDER BY MAGAMY DESC, MAGAMM DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
