mapper.select:
SELECT COUNT(*) FROM( SELECT TO_CHAR(payDate,'YYYY-MM-DD') payDate FROM payment p JOIN payInfo pi ON p.payCode = pi.payCode JOIN paymentInfo pmi ON p.payCode = pmi.payCode JOIN goods gs ON pmi.goodsCode = gs.goodsCode JOIN goodsGubun gg ON gs.gubunCode = gg.gubunCode JOIN member1 m1 ON m1.usersCode = p.usersCode WHERE TO_CHAR(payDate,'YYYY') LIKE #{year} AND parentCode = #{thema}
AND
GROUP BY TO_CHAR(payDate,'YYYY-MM-DD') ORDER BY payDate )

SELECT * FROM( SELECT p.payCode, payDate, payWay, payPrice, quantity, goodsName, gg.gubunCode, gs.price, gubunName, parentCode, cardCo, paySection, ci.state, DENSE_RANK() OVER(ORDER BY TO_CHAR(payDate,'YYYY-MM-DD') DESC) rnum, (SELECT COUNT(*) FROM couponHistory WHERE payCode = p.payCode) AS couponCount, memberId FROM payment p JOIN payInfo pi ON p.payCode = pi.payCode JOIN paymentInfo pmi ON p.payCode = pmi.payCode JOIN goods gs ON pmi.goodsCode = gs.goodsCode JOIN goodsGubun gg ON gs.gubunCode = gg.gubunCode JOIN cardInfo ci ON p.payCode = ci.payCode JOIN member1 m1 ON m1.usersCode = p.usersCode WHERE TO_CHAR(payDate,'YYYY') LIKE #{year}
AND
)
WHERE rnum >= #{start} AND rnum <= #{end}
AND parentCode = #{thema} ORDER BY payDate DESC, parentCode

SELECT pp.payCode FROM payment pp LEFT OUTER JOIN gift g ON pp.payCode = g.payCode LEFT OUTER JOIN ticket t ON pp.payCode = t.payCode WHERE pp.payCode NOT IN( SELECT p.payCode payCodelist FROM payment p LEFT OUTER JOIN gift g ON p.payCode = g.payCode LEFT OUTER JOIN ticket t ON p.payCode = t.payCode WHERE (useDate IS NOT NULL
OR TO_CHAR(g.endDate,'YYYY-MM-DD') < #{endDate}) OR TO_CHAR(t.startDate,'YYYY-MM-DD') < #{endDate}
GROUP BY p.payCode ) GROUP BY pp.payCode
