SELECT * FROM(
    SELECT ROWNUM rnum, tb.* FROM(
        SELECT q.qnaCode, gubun, subject, TO_CHAR(qDate,'YYYY-MM-DD') qDate, q.parentCode, a.qnaCode as answer
        FROM question q
        LEFT OUTER JOIN (
            SELECT qnaCode, parentCode
            FROM question 
            WHERE parentCode != 0
        )a ON a.parentCode=q.qnaCode  

    )tb WHERE ROWNUM <= #{end} AND gubun = #{gubun}
    ORDER BY qDate DESC
)WHERE rnum >= #{start} AND parentCode = 0

		SELECT * FROM(
		    SELECT ROWNUM rnum, tb.* FROM(
		        SELECT q.qnaCode, gubun, subject, TO_CHAR(qDate,'YYYY-MM-DD') qDate, q.parentCode, a.qnaCode as answer
		        FROM question q
		        LEFT OUTER JOIN (
		            SELECT qnaCode, parentCode
		            FROM question 
		            WHERE parentCode != 0
		        )a ON a.parentCode=q.qnaCode  
		
		    )tb WHERE ROWNUM <= #{end}
		    ORDER BY qDate DESC
		)WHERE rnum >= #{start} AND parentCode = 0 AND gubun = #{gubun}
		
		 AND qnaCode NOT IN(
        SELECT parentCode
        FROM question 
        WHERE parentCode != 0)

SELECT qnaCode, gubun, subject, content, TO_CHAR(qDate,'YYYY-MM-DD') qDate, parentCode, usersCode
FROM question
WHERE qnaCode = #{num}
