mapper.select:
SELECT NVL(COUNT(*),0)
FROM USERS
WHERE AUTHORITY='ROLE_ADMIN' AND USERSCODE = #{num}

SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT S.USERSCODE, STAFFID, NAME, TEL, TO_CHAR(BIRTH,'YYYY-MM-DD') BIRTH, POST, ADDR1, ADDR2, EMAIL, ORIGINALFILENAME, SAVEFILENAME, TASK, WORKING, AUTHORITY, D.DPCODE, DPNAME, P.POSITIONCODE, POSITIONNAME, T.THEMECODE, THEMENAME FROM STAFF S JOIN STAFFINFO SI ON S.USERSCODE = SI.USERSCODE JOIN DEPARTMENT D ON D.DPCODE = S.DPCODE JOIN POSITION P ON P.POSITIONCODE = S.POSITIONCODE JOIN THEME T ON T.THEMECODE = S.THEMECODE
ORDER BY S.USERSCODE DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

mapper.update:
UPDATE STAFF SET NAME = #{name}, TEL = #{tel},
BIRTH = #{birth},EMAIL = #{email}, 
POST = #{post}, ADDR1 = #{addr1}, ADDR2 = #{addr2} 
WHERE USERSCODE = #{usersCode}

mapper.select:
SELECT THEMECODE 
FROM THEME 
WHERE THEMENAME = #{themeName}

SELECT DPCODE 
FROM DEPARTMENT 
WHERE DPNAME = #{dpName}

SELECT POSITIONCODE 
FROM POSITION 
WHERE POSITIONNAME = #{positionName}
