mapper.select:
SELECT ROWNUM AS RNUM, to_char(PAYDATE,'yyyy-mm-dd') payDate, totalCount from ( SELECT PAYDATE, sum(totalCount) as totalCount from(
select PAYDATE, sum(quantity) as totalCount from ( select p.PAYDATE, quantity, g.gubunCode, p.payCode , c.state from PAYMENT p join paymentInfo pi on p.payCode = pi.payCode join cardInfo c on p.payCode = c.payCode join goods g on g.goodsCode = pi.goodsCode join goodsGubun gg on gg.gubunCode = g.gubunCode WHERE gg.parentCode=1 and g.gubunCode != 8 ) WHERE STATE = '카드승인' group by rollup(PAYDATE) union all select useDate as payDate, sum(totalCount) as totalCount from ( select useDate, 1 as totalCount from ticketHistory p ) group by rollup(useDate) ) group by rollup(PAYDATE) order by PAYDATE )WHERE PAYDATE IS NOT NULL
AND PAYDATE >= #{startDate} AND PAYDATE <= #{endDate}

		SELECT to_char(useDate, 'yyyy-mm-dd') payDate, totalCount
		FROM 
		    (
		        select useDate, sum(quantity) as totalCount
		        from ( 
		                 select useDate, 1 as quantity
		                        from ticketHistory
		                  )
		        group by rollup(useDate) 
        )WHERE useDate IS NOT NULL
        
		 AND useDate >= #{startDate} AND useDate <= #{endDate}
